mapper.select:
select id, userid as userid, title, description
from sc_item
where id=#{id}

select id, userid, code, title, weight, description
from sc_item a 
where userid=#{userId} limit #{start},#{offset}

insert into sc_item_mapping(
sc_item_id, tb_item_id, tb_item_title, tb_item_detailurl) 
values (
#{itemId}, #{tbitemId}, #{tbItemTitle}, #{tbItemUrl}
)

select tb_item_id as id, tb_item_title as title, tb_item_detailurl as detailurl
from sc_item_mapping
where sc_item_id = #{id}
